require_once('workflows.php');
$wf = new Workflows();

$orig = "{query}";
$json = $wf->request( "http://itunes.apple.com/search?country=cn&entity=macSoftware&limit=25&term=".urlencode( $orig ) );
$json = json_decode( $json, ture);
$int = 1;
$json=$json['results'];

$wf->result( "searchinstore", "macappstore://ax.search.itunes.apple.com/WebObjects/MZSearch.woa/wa/search?q=".urlencode( $orig ), 'Search apps with keyword "'.$orig.'"', 'Open iTunes and search apps with what you type.', "icon.png"  );

//*
foreach( $json as $sugg ):
	$data = $sugg['trackName'];
	$url = preg_replace("/^https?/", "macappstore", $sugg['trackViewUrl']);
	//$icon = $sugg['artworkUrl60'];
	$icon = "icon.png";
	$wf->result( 
		$int.'.'.time()
		, "$url"
		, "$data"
		, $sugg['formattedPrice']
			.'  	Version '.$sugg['version']
			.'  	Ratings  '.number_format($sugg['averageUserRating'], 1, '.', '')
			.' / 5	from '.number_format($sugg['userRatingCount'])
			.' users  by '.$sugg['artistName']
		, $icon  );
	$int++;
endforeach;

$results = $wf->results();
if ( count( $results ) == 1 ):
	$wf->popresults();
	$wf->result( 'googlesuggest', 'https://www.google.com/search?ie=UTF-8&oe=UTF-8&q='.urlencode( $orig ), 'Nothing Found', 'No search apps found. Search Google for '.$orig, 'icon.png' );
endif;

echo $wf->toxml();

require_once('workflows.php');
$wf = new Workflows();

$orig = "{query}";
$json = $wf->request( "http://itunes.apple.com/search?country=cn&entity=software&limit=25&term=".urlencode( $orig ) );
$json = json_decode( $json, ture);
$int = 1;
$json=$json['results'];

$wf->result( "searchinstore", "itunes://ax.search.itunes.apple.com/WebObjects/MZSearch.woa/wa/search?term=".urlencode( $orig ), 'Search apps with keyword "'.$orig.'"', 'Open iTunes and search apps with what you type.', "icon.png"  );


foreach( $json as $sugg ):
	$data = $sugg['trackCensoredName'];
	$url = preg_replace("/^https?/", "itunes", $sugg['trackViewUrl']);

	$type = "for iPhone	";

	if( in_array("iosUniversal", $sugg['features']) ):
		$type = "Universal	";
	endif;

	if( count($sugg['screenshotUrls']) == 0):
		$type = "for iPad	";
	endif;

	$wf->result( 
		$int.'.'.time()
		, "$url", "$data"
		, $type.' '.$sugg['formattedPrice']
			.' 	Version '.$sugg['version']
			.'  	Ratings : '.number_format($sugg['averageUserRating'], 1, '.', '')
			.' / 5	from '.number_format($sugg['userRatingCount'])
			.' users  by '.$sugg['artistName']
		, "icon.png"  );
	$int++;
endforeach;

$results = $wf->results();
if ( count( $results ) == 1 ):

	$wf->popresults();
	$wf->result( 'googlesuggest', 'https://www.google.com/search?ie=UTF-8&oe=UTF-8&q='.urlencode( $orig ), 'Nothing Found', 'No search apps found. Search Google for '.$orig, 'GoogleIcon.png' );
endif;

echo $wf->toxml();
